import { Button, Container, Form, Row, Col } from "react-bootstrap";
import { useEffect, useState } from "react";
import Student from "./Student";

const Classroom = () => {
  const [students, setStudent] = useState([]);
  const [studentName, setStuName] = useState("");
  const [studentMajor, setStuMajor] = useState("");
  const [studentIntrest, setStuInt] = useState("");
  const [shownStudents, setshownStudents] = useState([]);

  useEffect(() => {
    fetch("https://cs571.org/s23/hw4/api/students", {
      headers: {
        "X-CS571-ID": "bid_d5f87cfbd68cea89ba78",
      },
    })
      .then((res) => res.json())
      .then((studentData) => {
        filter(studentData);
        setStudent(studentData);
        console.log(shownStudents);
        console.log(studentData);
      });
  }, []);

  function filter(studentData) {
    console.log(studentName);
    console.log(studentMajor);
    console.log(studentIntrest);
    if (
      studentName === null &&
      studentMajor === null &&
      studentIntrest === null
    ) {
      return;
    }

    let searchName = studentName
      .toLowerCase()
      .split(" ")
      .filter(function (token) {
        return token.trim() !== "";
      });

    let searchMajor = studentMajor
      .toLowerCase()
      .split(" ")
      .filter(function (token) {
        return token.trim() !== "";
      });

    let searchInterest = studentIntrest
      .toLowerCase()
      .split(" ")
      .filter(function (token) {
        return token.trim() !== "";
      });

    setshownStudents(
      studentData.reduce((prevStudent, currStudent) => {
        if (
          currStudent.name.first.toLowerCase().includes(searchName) ||
          currStudent.name.last.toLowerCase().includes(searchName)
        ) {
          if (currStudent.major.toLowerCase().includes(searchMajor)) {
            let lowerInt = currStudent.interests.map((interest) =>
              interest.toLowerCase().includes(searchInterest)
            );
            if (lowerInt.includes(true)) {
              //everything inputted
              prevStudent.push(currStudent);
            } else if (searchInterest === "") {
              //only major and name inputted
              prevStudent.push(currStudent);
            }
          } else if (
            searchMajor === "" &&
            currStudent.interests
              .map((interest) =>
                interest.toLowerCase().includes(searchInterest)
              )
              .includes(true)
          ) {
            //interest and name inputted
            prevStudent.push(currStudent);
          } else if (searchMajor === "" && searchInterest === "") {
            // only name inputted
            prevStudent.push(currStudent);
          }
        } else if (
          currStudent.major.toLowerCase().includes(searchMajor) &&
          searchName === ""
        ) {
          let lowerInt = currStudent.interests.map((interest) =>
            interest.toLowerCase().includes(searchInterest)
          );
          if (lowerInt.includes(true)) {
            // with major and interest
            prevStudent.push(currStudent);
          } else if (searchInterest == "") {
            // students with major inputted
            prevStudent.push(currStudent);
          }
        } else if (
          currStudent.interests
            .map((interest) => interest.toLowerCase().includes(searchInterest))
            .includes(true) &&
          searchName === "" &&
          searchMajor === ""
        ) {
          // same interest, noting else inputted
          prevStudent.push(currStudent);
        }
      }, [])
    );
  }

  return (
    <div>
      <Form>
        <Form.Label htmlFor="searchName">Name</Form.Label>
        <Form.Control
          id="searchName"
          value={studentName}
          onChange={(e) => setStuName(e.target.value)}
        />
        <Form.Label htmlFor="searchMajor">Major</Form.Label>
        <Form.Control
          id="searchMajor"
          value={studentMajor}
          onChange={(e) => setStuMajor(e.target.value)}
        />
        <Form.Label htmlFor="searchInterest">Interest</Form.Label>
        <Form.Control
          id="searchInterest"
          value={studentIntrest}
          onChange={(e) => setStuInt(e.target.value)}
        />
        <br />
        <Button variant="neutral">Reset Search</Button>
      </Form>
      <Container fluid>
        <Row>
          {students.map((student) => {
            return (
              <Col xs={12} sm={6} md={4} lg={3} xl={2} key={student.id}>
                <Student {...student}></Student>
              </Col>
            );
          })}
        </Row>
      </Container>
    </div>
  );
};

export default Classroom;
