import { Button, Container, Form, Row, Col } from "react-bootstrap";
import { useEffect, useState } from "react";
import Student from "./Student";

const Classroom = () => {
  const [students, setStudent] = useState([]);
  const [studentName, setStuName] = useState("");
  const [studentMajor, setStuMajor] = useState("");
  const [studentInterest, setStuInt] = useState("");
  const [shownStudents, setShownStudents] = useState([]);

  useEffect(() => {
    fetch("https://cs571.org/s23/hw4/api/students", {
      headers: {
        "X-CS571-ID": "bid_d5f87cfbd68cea89ba78",
      },
    })
      .then((res) => res.json())
      .then((studentData) => {
        setStudent(studentData);
        setShownStudents(studentData);
        console.log(studentData);
      });
  }, []);

  function filterData() {
    if (
      studentName === null &&
      studentMajor === null &&
      studentInterest === null
    ) {
      return;
    }

    setStudent(shownStudents.filter(studentFilter));
  }

  function studentFilter(student) {
    let searchName = studentName
      .toLowerCase()
      .split(" ")
      .filter(function (token) {
        return token.trim() !== "";
      });

    console.log(searchName);

    let searchMajor = studentMajor
      .toLowerCase()
      .split(" ")
      .filter(function (token) {
        return token.trim() !== "";
      });

    console.log(searchMajor);

    let searchInterest = studentInterest
      .toLowerCase()
      .split(" ")
      .filter(function (token) {
        return token.trim() !== "";
      });

    console.log(searchInterest);

    if (
      student.name.first.toLowerCase().includes(searchName) ||
      student.name.last.toLowerCase().includes(searchName)
    ) {
      if (student.major.toLowerCase().includes(searchMajor)) {
        let lowerInt = student.interests.map((interest) =>
          interest.toLowerCase().includes(searchInterest)
        );
        if (lowerInt.includes(true)) {
          //everything inputted
          return student;
        } else if (searchInterest === "") {
          //only major and name inputted
          return student;
        }
      } else if (
        searchMajor === "" &&
        student.interests
          .map((interest) => interest.toLowerCase().includes(searchInterest))
          .includes(true)
      ) {
        //interest and name inputted
        return student;
      } else if (searchMajor === "" && searchInterest === "") {
        // only name inputted
        return student;
      }
    } else if (
      student.major.toLowerCase().includes(searchMajor) &&
      searchName === ""
    ) {
      let lowerInt = student.interests.map((interest) =>
        interest.toLowerCase().includes(searchInterest)
      );
      if (lowerInt.includes(true)) {
        // with major and interest
        return student;
      } else if (searchInterest == "") {
        // students with major inputted
        return student;
      }
    } else if (
      student.interests
        .map((interest) => interest.toLowerCase().includes(searchInterest))
        .includes(true) &&
      searchName === "" &&
      searchMajor === ""
    ) {
      // same interest, noting else inputted
      return student;
    }
  }

  return (
    <div>
      <Form>
        <Form.Label htmlFor="searchName">Name</Form.Label>
        <Form.Control
          id="searchName"
          value={studentName}
          onChange={(e) => {
            setStuName(e.target.value);
            filterData();
          }}
        />
        <Form.Label htmlFor="searchMajor">Major</Form.Label>
        <Form.Control
          id="searchMajor"
          value={studentMajor}
          onChange={(e) => {
            setStuMajor(e.target.value);
            filterData();
          }}
        />
        <Form.Label htmlFor="searchInterest">Interest</Form.Label>
        <Form.Control
          id="searchInterest"
          value={studentInterest}
          onChange={(e) => {
            setStuInt(e.target.value);
            filterData();
          }}
        />
        <br />
        <Button variant="neutral" onclick="allClear()">
          Reset Search
        </Button>
      </Form>
      <Container fluid>
        <Row>
          {students.map((student) => {
            return (
              <Col xs={12} sm={6} md={4} lg={3} xl={2} key={student.id}>
                <Student {...student}></Student>
              </Col>
            );
          })}
        </Row>
      </Container>
    </div>
  );
};

export default Classroom;
