import { Card } from "react-bootstrap";

const Song = (props) => {
  return (
    <Card>
      <img src={props.img} alt="Italian Trulli"></img>

      <p>I am a song!</p>
      <h2 style={{ fontSize: "1.25rem" }}>{props.title}</h2>
      <p>{props.description}</p>
    </Card>
  );
};

export default Song;
