import { useContext, useState, useEffect } from "react";
import { Col, Container, Row } from "react-bootstrap";
import BadgerBeatsFavoritesContext from "../contexts/BadgerBeatsFavoritesContext";
import Song from "./Song";

const AllSongs = (props) => {
  const [favorites, setFavorites] = useContext(BadgerBeatsFavoritesContext);
  const [songs, setSongs] = useState([]);
  const [active, setActive] = useState(false);

  useEffect(() => {
    fetch("https://cs571.org/s23/hw5/api/songs", {
      headers: {
        "X-CS571-ID": "bid_d5f87cfbd68cea89ba78",
      },
    })
      .then((data) => data.json())
      .then((songData) => {
        setSongs(songData);
        console.log(songData);
      });
  }, []);

  const uniqueGenre = [...new Set(songs.map((song) => song["genre"]))];

  const totalTime = songs.reduce((prevSongTime, curSongTime) => {
    let time = curSongTime["length"].split(":");
    let seconds = parseInt(time[1]);
    let minCovert = parseInt(time[0]) * 60;
    return prevSongTime + minCovert + seconds;
  }, 0);

  const buttonStatus = (buttonChange) => {
    setActive(buttonChange);
  };

  return (
    <div>
      <h1>Songs</h1>
      <p>
        We have {songs.length} songs in {uniqueGenre.length} genres for a total
        of {totalTime} seconds of music!
      </p>
      <Container fluid>
        <Row>
          {songs.map((songData) => {
            return (
              <Col xs={12} sm={6} md={4} lg={3} xl={2}>
                <Song
                  song={songData}
                  key={songData.id}
                  move={move}
                  status={buttonStatus}
                ></Song>
              </Col>
            );
          })}
        </Row>
      </Container>
    </div>
  );
};

export default AllSongs;
