import { Card, Button } from "react-bootstrap";
import { useState, useEffect, useContext } from "react";
import BadgerBeatsFavoritesContext from "../contexts/BadgerBeatsFavoritesContext";

const Song = (props) => {
  const [active, setActive] = useState(false);
  const [favorites, setFavorites] = useContext(BadgerBeatsFavoritesContext);

  const modifyButton = () => {
    setActive(!active);
    setFavorites(props.song);
    // if (active == false) {
    //   console.log("Active false");
    //   console.log("Props ID" + props.song.id);
    //   setFavorites(favorites.filter((fav) => fav.id !== props.song.id));
    //   console.log(favorites);
    // } else {
    //   console.log("Active true");
    //   setFavorites([...favorites, props.song]);
    // }
  };

  return (
    <Card>
      <img src={props.song.img} alt={props.song.title}></img>
      <h3 style={{ paddingLeft: "0.2rem" }}>{props.song.title}</h3>
      <h5 style={{ paddingLeft: "0.2rem" }}>by {props.song.artist}</h5>
      <p style={{ paddingLeft: "0.2rem", fontSize: "1rem" }}>
        {props.song.genre} | {props.song.year} | {props.song.length}
      </p>
      <div style={{ paddingLeft: "0.2rem", paddingBottom: "0.2rem" }}>
        <Button
          type="button"
          variant={active ? "danger" : "primary"}
          style={{ padding: "0.2rem" }}
          onClick={() => {
            modifyButton();
          }}
        >
          {active ? "Remove from Favorites" : "Add to Favorites"}
        </Button>
      </div>
    </Card>
  );
};

export default Song;
