import { Card } from "react-bootstrap";

const [beforeText, setText] = useState("Add to Favorites");
const [active, setActive] = useState(false);

const modifyButton = () => {
  setActive(!active);
};
const modifyText = (text) => setText(text);

const Song = (props) => {
  return (
    <Card>
      <img src={props.song.img} alt={props.song.title}></img>
      <h3 style={{ paddingLeft: "0.2rem" }}>{props.song.title}</h3>
      <h5 style={{ paddingLeft: "0.2rem" }}>by {props.song.artist}</h5>
      <p style={{ paddingLeft: "0.2rem", fontSize: "1rem" }}>
        {props.song.genre} | {props.song.year} | {props.song.length}
      </p>
      <div style={{ paddingLeft: "0.2rem", paddingBottom: "0.2rem" }}>
        <button
          type="button"
          style={{ padding: "0.2rem", backgroundColor: "blue" }}
          onClick={() => {
            modifyButton();
          }}
        >
          Add to Favorites
        </button>
      </div>
    </Card>
  );
};

export default Song;
