import { Card, Button } from "react-bootstrap";
import { useState, useContext } from "react";
import BadgerBeatsFavoritesContext from "../contexts/BadgerBeatsFavoritesContext";

const Favorite = (props) => {
  const [favorites, setFavorites] = useContext(BadgerBeatsFavoritesContext);

  const removeButton = () => {
    const filteredSong = favorites.filter(
      (favoriteSong) => favoriteSong.id !== props.favSong.id
    );
    setFavorites(filteredSong);
  };

  return (
    <Card>
      <img src={props.favSong.img} alt={props.favSong.title}></img>
      <h3 style={{ paddingLeft: "0.2rem" }}>{props.favSong.title}</h3>
      <h5 style={{ paddingLeft: "0.2rem" }}>by {props.favSong.artist}</h5>
      <p style={{ paddingLeft: "0.2rem", fontSize: "1rem" }}>
        {props.favSong.genre} | {props.favSong.year} | {props.favSong.length}
      </p>
      <div style={{ paddingLeft: "0.2rem", paddingBottom: "0.2rem" }}>
        <Button
          type="button"
          variant="danger"
          style={{ padding: "0.2rem" }}
          onClick={removeButton}
        >
          "Remove from Favorites"
        </Button>
      </div>
    </Card>
  );
};

export default Favorite;
