import { useContext } from "react";
import BadgerBeatsFavoritesContext from "../contexts/BadgerBeatsFavoritesContext";

const FavoriteSongs = (props) => {
  const [favorites, setFavorites] = useContext(BadgerBeatsFavoritesContext);

  return (
    <div>
      <h1>Favorites</h1>
      <p>
        You have favorited XX songs in YY genres for a total of ZZ seconds of
        music!
      </p>
      <BadgerBeatsFavoritesContext.Provider value={[favorites, setFavorites]}>
        {favorites.map((fav) => {
          return (
            <Card>
              <img src={fav.img} alt={fav.title}></img>
              <h3 style={{ paddingLeft: "0.2rem" }}>{fav.title}</h3>
              <h5 style={{ paddingLeft: "0.2rem" }}>by {fav.artist}</h5>
              <p style={{ paddingLeft: "0.2rem", fontSize: "1rem" }}>
                {fav.genre} | {fav.year} | {fav.length}
              </p>
              <div style={{ paddingLeft: "0.2rem", paddingBottom: "0.2rem" }}>
                <Button
                  type="button"
                  variant={"danger"}
                  style={{ padding: "0.2rem" }}
                  onClick={() => {
                    removeButton();
                  }}
                >
                  Remove from Favorites
                </Button>
              </div>
            </Card>
          );
        })}
      </BadgerBeatsFavoritesContext.Provider>
    </div>
  );
};

export default FavoriteSongs;
