import { useContext, useEffect } from "react";
import { Col, Container, Row } from "react-bootstrap";
import BadgerBeatsFavoritesContext from "../contexts/BadgerBeatsFavoritesContext";
import Favorite from "./Favorite";

const FavoriteSongs = (props) => {
  const [favorites, setFavorites] = useContext(BadgerBeatsFavoritesContext);
  const [songs, setSongs] = useState([]);

  useEffect(() => {
    fetch("https://cs571.org/s23/hw5/api/songs", {
      headers: {
        "X-CS571-ID": "bid_d5f87cfbd68cea89ba78",
      },
    })
      .then((data) => data.json())
      .then((songData) => {
        setSongs(songData);
      });
  }, []);

  const uniqueGenre = [
    ...new Set(favorites.map((favSong) => favSong["genre"])),
  ];

  const totalTime = favorites.reduce((prevSongTime, curSongTime) => {
    let time = curSongTime["length"].split(":");
    let seconds = parseInt(time[1]);
    let minCovert = parseInt(time[0]) * 60;
    return prevSongTime + minCovert + seconds;
  }, 0);

  return (
    <div>
      <h1>Favorites</h1>
      <p>
        You have favorited {favorites.length} songs in {uniqueGenre.length}{" "}
        genres for a total of {totalTime} seconds of music!
      </p>
      <Container fluid>
        <Row>
          {favorites.map((favSongs) => {
            return (
              <Col xs={12} sm={6} md={4} lg={3} xl={2} key={favSongs.id}>
                <Favorite favSong={favSongs}></Favorite>
              </Col>
            );
          })}
        </Row>
      </Container>
    </div>
  );
};

export default FavoriteSongs;
