import { useContext, useEffect } from "react";
import BadgerBeatsFavoritesContext from "../contexts/BadgerBeatsFavoritesContext";
import { Card, Button } from "react-bootstrap";
import Song from "./Song";

const FavoriteSongs = (props) => {
  const [favorites, setFavorites] = useContext(BadgerBeatsFavoritesContext);
  const [songs, setSongs] = useState([]);

  useEffect(() => {
    fetch("https://cs571.org/s23/hw5/api/songs", {
      headers: {
        "X-CS571-ID": "bid_d5f87cfbd68cea89ba78",
      },
    })
      .then((data) => data.json())
      .then((songData) => {
        setSongs(songData);
        console.log(songData);
      });
  }, []);

  const removeButton = () => {};

  return (
    <div>
      <h1>Favorites</h1>
      <p>
        You have favorited XX songs in YY genres for a total of ZZ seconds of
        music!
      </p>
      <BadgerBeatsFavoritesContext.Provider value={[favorites, setFavorites]}>
        {favorites.map((fav) => {
          return (
            <Container fluid>
              <Row>
                {songs.map((songData) => {
                  return (
                    <Col xs={12} sm={6} md={4} lg={3} xl={2}>
                      <Song
                        song={songData}
                        key={songData.id}
                        //   move={move}
                      ></Song>
                    </Col>
                  );
                })}
              </Row>
            </Container>
          );
        })}
      </BadgerBeatsFavoritesContext.Provider>
    </div>
  );
};

export default FavoriteSongs;
