import React, { useState } from "react";
import { Form, Button } from "react-bootstrap";

export default function BadgerRegister() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [registrationStatus, setRegistrationStatus] = useState("");

  const handleRegister = () => {
    if (!username || !password) {
      alert("You must provide both a username and password!");
      return;
    }

    if (password !== confirmPassword) {
      alert("Your passwords do not match!");
      return;
    }

    console.log("After checks");

    useEffect();
    const url = "https://cs571.org/s23/hw6/api/register";
    fetch(url, {
      method: "POST",
      credentials: "include",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        username: { username },
        password: { password },
      }),
    }).then((res) => {
      if (res.status === 200) {
        setRegistrationStatus("success");
        setUsername("");
        setPassword("");
        setConfirmPassword("");
      } else if (res.status === 409) {
        setRegistrationStatus("usernameTaken");
      }
    });
  };

  useEffect(() => {
    if (registrationStatus === "success") {
      alert("Registration successful!");
    } else if (registrationStatus === "usernameTaken") {
      alert("That username has already been taken!");
    }
  }, [registrationStatus]);

  return (
    <>
      <h1>Register</h1>
      <Form onSubmit={handleRegister}>
        <Form.Group controlId="formUsername" style={{ marginBottom: "1rem" }}>
          <Form.Label>Username</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </Form.Group>
        <Form.Group controlId="formPassword" style={{ marginBottom: "1rem" }}>
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </Form.Group>
        <Form.Group
          controlId="formConfirmPassword"
          style={{ marginBottom: "1rem" }}
        >
          <Form.Label>Confirm Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Confirm Password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
          />
        </Form.Group>
        <Button variant="primary" type="submit">
          Register
        </Button>
      </Form>
    </>
  );
}
