import React, { useContext } from "react";
import { Container, Nav, Navbar, NavDropdown } from "react-bootstrap";
import { Link, Outlet } from "react-router-dom";

import crest from "../../assets/uw-crest.svg";
import pageStatus from "../../contexts/isLoggedInContext";

function BadgerLayout(props) {
  const [isLoggedIn, setIsLoggedIn] = useContext(pageStatus);

  return (
    <div>
      <Navbar bg="dark" variant="dark">
        <Container>
          <Navbar.Brand as={Link} to="/">
            <img
              alt="BadgerChat Logo"
              src={crest}
              width="30"
              height="30"
              className="d-inline-block align-top"
            />{" "}
            BadgerChat
          </Navbar.Brand>
          <Nav className="me-auto">
            <Nav.Link as={Link} to="/">
              Home
            </Nav.Link>
            {!isLoggedIn ? (
              <>
                <Nav.Link as={Link} to="login">
                  Login
                </Nav.Link>

                <Nav.Link as={Link} to="register">
                  Register
                </Nav.Link>
              </>
            ) : null}
            <NavDropdown title="Chatrooms">
              {props.chatrooms.map((chatroom, index) => {
                return (
                  <React.Fragment key={chatroom}>
                    <NavDropdown.Item as={Link} to={`chatrooms/${chatroom}`}>
                      {chatroom}
                    </NavDropdown.Item>
                    {index !== props.chatrooms.length - 1 && (
                      <NavDropdown.Divider key={chatroom} />
                    )}
                  </React.Fragment>
                );
              })}
            </NavDropdown>
            {!isLoggedIn ? <b>logged out</b> : <b>logged in</b>}
          </Nav>
        </Container>
      </Navbar>
      <div className="body-spacer">
        <Outlet />
      </div>
    </div>
  );
}

export default BadgerLayout;
